$(document).ready(function() {
    var lang_code = localStorage.getItem("lang_code");
    if (lang_code == "ar" || lang_code == "arc" || lang_code == "az" || lang_code == "dv" || lang_code == "iw" || lang_code == "ku" ||
        lang_code == "fa" || lang_code == "ur") {
        $("body").attr("dir", "rtl");
        $('head').append('<link rel="stylesheet" href="css/rtl.min.css" type="text/css" />');
        //navbar remove class for RTL 
        $(".navbar-nav").removeClass("ml-auto");
        $(".navbar-nav").addClass("mr-auto");
    } else {
        $("body").removeAttr("dir");
    }
})